const fs = require("fs")
const executor = require('./executor')
import * as discord from 'discord.js'
export = {
    find : (commandName: string, client: discord.Client, msg:discord.Message, args:string[], commandDir: string) => {
        const cmd = fs.readdirSync(`${commandDir}`)
        for(let i = 0; i < cmd.length; i++){
            const load = require(`../comandos/${cmd[i]}`)
            if(load.name == commandName){
                executor.execute(cmd[i], client, msg, args)
                return
            }else{
                if(!load.aliases) return
                for(let o = 0; o < load.aliases.length; o++){
                    if(load.aliases[o] == commandName){
                        
                        executor.execute(cmd[i], client, msg, args)
                        return
                    }
                }
            }
            
        }
    }
}