import * as fs from 'fs'
exports = {
    handle : (commandDir: string) => {
        const cmd = fs.readdirSync(`${commandDir}/`)
        for(let i = 0; i < cmd.length; i++){
            const load = require(`${commandDir}/${cmd[i]}`)
            if(!load.name){
                console.log(`could not load ${cmd[i]}. ERRO: Missing Name`)
            }else{
                console.log(`Loaded command ${load.name}`)
            }

        }
    }
}